CommonData.PCCustomCSS:
/* Global Styles */
#onetrust-pc-sdk {
    height: 100% !important;
    max-width: 100% !important;
    min-width: 100% !important;
    position: fixed !important;
    top: 0 !important;
    background-color: #f7f7f7 !important;
}

/* Header Styles */
#onetrust-pc-sdk .ot-pc-header {
    background: #333;
    height: 55px;
    margin: 0;
    padding: 0;
    padding-left: max(20px, 50% - 544px);
    padding-right: 0;
}

[dir="rtl"] #onetrust-pc-sdk .ot-pc-header {
    padding-left: 0;
    padding-right: max(20px, 50% - 544px);
}

/* Logo Styles */
#onetrust-pc-sdk .ot-pc-logo {
    background-color: #d71921;
    background-image: url(https://c.ekstatic.net/ecl/logos/emirates/emirates-logo-badge.svg) !important;
    background-size: 90px 125px;
    background-position-y: 27px !important;
    height: 125px;
    margin: 0;
    position: relative;
    width: 90px;
    z-index: 2;
}

#onetrust-pc-sdk .ot-pc-logo img {
    display: none;
}               

/* Close Button Styles */
#onetrust-pc-sdk #close-pc-btn-handler.ot-close-icon,
#onetrust-consent-sdk > #onetrust-pc-sdk #close-pc-btn-handler {
    width: 184px;
    width: min(48% - 20px, 184px);
    height: 40px;
    border-radius: 3px;
    box-shadow: 0 2px 1px 0 rgba(0, 0, 0, 0.1);
    color: #424242;
    border: solid 1px #333333;
    background-color: #ffffff;
    background-image: none !important;
    margin: 0;
    position: fixed;
    left: max(20px, 50% - 543px);
    bottom: 7px;
    top: auto;
    right: auto;
    z-index: 102;
}

[dir="rtl"] #onetrust-consent-sdk > #onetrust-pc-sdk #close-pc-btn-handler,
[lang^=ar] #onetrust-consent-sdk > #onetrust-pc-sdk #close-pc-btn-handler {
    left: auto;
    right: max(20px, 50% - 543px);
}

#onetrust-consent-sdk > #onetrust-pc-sdk #close-pc-btn-handler::after {
    content: attr(aria-label) ' ';
    font-size: 14px;
    font-weight: bold;
    font-family: HelveticaNeue, Helvetica;
    letter-spacing: normal !important;
    text-align: center;
}


/* Content Styles */
#onetrust-pc-sdk #ot-pc-content {
    height: 77vh;
    left: 0;
    margin: 0;
    overflow-x: clip;
    overflow-y: scroll;
    padding-top: 85px;
    padding-left: max(20px, 50% - 544px);
    padding-right: max(20px, 50% - 544px);
    top: 55px;
    width: auto;
}

#onetrust-pc-sdk #ot-pc-content:after {
    content: ' ';
    background-image: url(https://c.ekstatic.net/shared/images/cookie-preference/worldmap-bg.png);
    background-repeat: no-repeat;
    background-attachment: local;
    background-position: 0 0;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-size: min(145%, 1900px) 720px;
    background-clip: content-box;
    overflow: hidden;
    z-index: -2;
}

/* Title and Description Styles */
#onetrust-consent-sdk #onetrust-pc-sdk #ot-pc-title,
#onetrust-consent-sdk #onetrust-pc-sdk #ot-pc-desc {
    color: #333;
    text-align: center;
}

#onetrust-consent-sdk #onetrust-pc-sdk #ot-pc-title {
    font-family: emirates-medium, 'emirates medium', Emirates-Bold, EmiratesSB-Bold, HelveticaNeue, Helvetica !important;
    font-size: 50px;
    font-weight: 300;
    letter-spacing: normal;
    line-height: 1.0;
    padding: 0 0px 10px 0px;
}

#onetrust-consent-sdk #onetrust-pc-sdk #ot-pc-desc {
    font-family: HelveticaNeue, Helvetica;
    font-size: 19px;
    font-weight: 300;
    width: calc(100% - 108px);
    margin: 0px 60px;
    line-height: normal;
}

/* Category Group Styles */
#onetrust-pc-sdk .ot-cat-grp {
    background-color: #ffffff;
    border-radius: 3px;
    box-shadow: 0 2px 4px 2px rgba(0, 0, 0, 0.1);
    display: flex;
    flex-direction: column;
    margin: 50px 0;
    padding: 50px;
    width: calc(100% - 108px);
}

#onetrust-pc-sdk .ot-accordion-layout.ot-cat-item {
    border: none !important;
    margin-bottom: 20px !important;
}

#onetrust-pc-sdk .ot-accordion-layout.ot-cat-item:last-child {
    margin-bottom: 0 !important;
}

#onetrust-pc-sdk .ot-accordion-layout.ot-cat-item svg {
    color: #d71921;
    width: 12px;
    height: 22px;
